<?phpfunction location($url) {    header("Location: ". $url);    exit;}function refresh() {    location(strtok($_SERVER["REQUEST_URI"], "?"));}function printsize($size) {    if ($size < 1024)        return round($size) .' o';    $size /= 1024;        if ($size < 1024)        return round($size) .' Ko';    $size /= 1024;        if ($size < 1024)        return round($size) .' Mo';            $size /= 1024;        return round($size, 2) .' Go';}